INSERT INTO `craft_matrixcontent_announcement` VALUES (1,123,1,'2018-08-14 19:42:54','2018-08-29 19:30:34','08360f63-9e1e-4bf8-b4c0-ae274ae49a7d','<p>Please feel free to stop by at any time (there will likely be several people there at the same time).</p>\n<p>Also, heres what is due for finals week:</p>',NULL,NULL,NULL,NULL),(2,124,1,'2018-08-14 19:50:08','2018-08-29 19:30:34','e7d1d0a7-8ed9-4b38-bbdf-3aea6f586369',NULL,NULL,NULL,NULL,NULL),(3,125,1,'2018-08-14 19:50:08','2018-08-29 19:30:34','d81829ee-e022-4fd3-8309-68dc516ed181','<h2>Remaining Assignments &amp; Deliverables</h2>\n<p>Here are the remaining items youll need to turn in for the semester.</p>\n<h3>Testing and Testing Results (5 pts)</h3>\n<p>We did technical, accessibility and usability testing in class last week. You should have turned in the link to the Google Sheet via Blackboard.</p>',NULL,NULL,NULL,NULL),(4,126,1,'2018-08-14 19:50:08','2018-08-29 19:30:34','9ad47ec1-7539-4598-b8fb-c04243fdc539',NULL,'View Testing Assignment','https://rwdkent.com/assignments/testing',NULL,NULL),(5,127,1,'2018-08-14 19:50:08','2018-08-29 19:30:34','e04ce34a-8a50-4694-a050-129c155e01f4',NULL,NULL,NULL,NULL,NULL),(6,128,1,'2018-08-14 19:50:08','2018-08-29 19:30:34','1068f46b-7f9a-43c1-a5de-7a8e8185a2da','<h3>Dec. 12 at 5:30 p.m. - Final Templates/Prototypes (15 pts)</h3>\n<p>Your 3 final prototypes should be completed and online via GitHub before the final class.</p>\n<p>Your prototypes should be linked together (so we can navigate them like a real site) but also linked from your Project Hub page.</p>\n<p>Youll turn in the link to your final prototype via Blackboard for grading on Dec. 12.</p>',NULL,NULL,NULL,NULL),(7,129,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','0254984b-2b94-47ac-9015-991703b8767f',NULL,'View Templates/Prototypes Assignment','https://rwdkent.com/assignments/templates',NULL,NULL),(8,130,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','25bcced5-ae25-4c7c-80a5-305f5b66dc27',NULL,NULL,NULL,NULL,NULL),(9,131,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','ec0ee693-c889-49e2-b342-fa029227323c','<h3>Dec. 12 at 5:30 p.m. - Project Hub, Pattern Library &amp; Review Session (5 pts)</h3>\n<p>This is the final 10-15 minute presentation that youll conduct on Tuesday of finals week (same as class time, 5:30 p.m.). During this time, youll demo all of your deliverables via your Project Hub page. The class will also use your website on multiple devices and give you feedback by completing a short survey for each presentation.</p>\n<p>This also includes a demo of a small Pattern Library showing 5 of your most common patterns used on the site. Please follow the instructions on the website.</p>\n<p>There is nothing to turn in on Blackboard for this part.</p>\n<p>Here are some guidelines for your sharing sessions:</p>\n<ul><li>Should Last About 10-15 minutes (these can be fairly informal)</li>\n<li>Bring up prototypes on multiple devices to pass around</li>\n<li>Bring Up Your Project Hub</li>\n<li>Brief History of Project (Goals, Process, etc.)</li>\n<li>Show Templates and Implementation at different breakpoints</li>\n<li>Show Main Components in Pattern Library</li>\n<li>Discuss Next Steps</li>\n<li>Get Feedback and Questions from Class/Client</li>\n</ul><p>During each presentation, each class member (and instructor) will use their own devices to submit an evaluation of the final project.</p>',NULL,NULL,NULL,NULL),(10,132,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','bc8ddc02-b6ee-41d6-8f2b-a4d616dbe370',NULL,'View Project Hub & Review Session Assignment','https://rwdkent.com/assignments/timeline-presentation',NULL,NULL),(11,133,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','09a02021-6c51-417b-a908-143deb35048b',NULL,NULL,NULL,NULL,NULL),(12,134,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','db49ea48-bcd1-436f-8d92-b573f0d1dc8b','<h2>Self &amp; Group Assessment</h2>\n<p>After Tuesday, youll still need to submit a separate survey that evaluates your individual and team performance. Please wait until youve submitted your projects before taking the survey. This is the last assignment youll submit for RWD!</p>',NULL,NULL,NULL,NULL),(13,135,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','0051eee7-d294-404f-a9c4-8f7183c13d77',NULL,'Access the Group/Self Assessment','https://rwdkent.com/assignments/assessment/',NULL,NULL),(14,136,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','d60222b8-ed27-4b2b-aa87-9f29eabae339',NULL,NULL,NULL,NULL,NULL),(15,137,1,'2018-08-14 19:50:09','2018-08-29 19:30:34','f772f7bc-a5b4-4686-8f04-3bcb9d4c44ca','<p>Please email me if you have any questions, and Ill see everyone on Tuesday for the final class!</p>',NULL,NULL,NULL,NULL);
