INSERT INTO `craft_matrixcontent_navigationitems` VALUES (1,81,1,'2018-08-07 19:16:55','2018-08-14 19:21:08','6fbabcfa-31ab-4cd4-9fc6-65d59e4fdfaf','Courses','/courses'),(2,82,1,'2018-08-07 19:16:55','2018-08-14 19:21:08','abc780ea-729e-4f88-844e-078f329d064e','Announcements','/announcements'),(3,83,1,'2018-08-07 19:16:55','2018-08-14 19:21:08','5b0ceb82-ad14-42d3-85dc-87f14c0790de','About','/about');
